package Operator;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Relational Operators ( <, >, <=, >=, ==, !=)
				// relational Operator is boolean type and always return true or false
				int c=10;
				int d=20;
				
				System.out.println(c<d); // c is  less than d - true
				System.out.println(c>d); // c is  grater than d - false
				System.out.println(c<=d); // c is less than and equal b - true(1st condition is true then ans is true)
				System.out.println(c>=d); // a is  grater and equal b - false(1st condition is false then ans is false)
				System.out.println(c==d); // a is  equal equal b - false
				System.out.println(c!=d); // a is  not equal to b - true

	}

}
